import React, { Component, useContext, useState } from 'react'
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import {UserContext} from "./UserContext"
import axios from 'axios';
import { Link } from 'react-router-dom';

export function Login() {
    const { user, setUser } = useContext(UserContext);

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const checkUser = async (username, password) => {
        const res = await axios.get('http://localhost:3001/users');
        const { data } = res;
        var arr = Object.values(data);
        for (var i = 0; i < arr.length; i++) {
            if (arr[i].username === username && arr[i].password === password) {
                return arr[i].email;
            }
        }
        return null;
    }
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log(username);
        console.log(password);
        const email = await checkUser(username, password);
        if (email === null) {
            console.log("Invalid details! Please try again")
        }
        console.log("User exists in the database!");
        const user = {email, username, password};
        console.log("User exists in the database!");
        const response = await axios.post('http://localhost:3001/users/login', user);
        const id = response.data._id;
        console.log("This is the id: ", id);
        const storeUser = {username, email, password, id};
        setUser(storeUser);
    }


    return (
        <div>
        <Container component="main" maxWidth="xs">
            <CssBaseline />
            <Box
            sx={{
                marginTop: 8,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
            }}
            >
            <Typography component="h1" variant="h3">
                Sign In
            </Typography>
            <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
                <TextField
                margin="normal"
                required
                fullWidth
                id="username"
                label="Username"
                name="username"
                autoComplete="username"
                onChange={(e) => setUsername(e.target.value)}
                autoFocus
                />
                <TextField
                margin="normal"
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="current-password"
                onChange={(e) => setPassword(e.target.value)}
                />
                <FormControlLabel
                control={<Checkbox value="remember" color="primary" />}
                label="Remember me"
                />
                <Button type="submit" fullWidth variant="outlined" sx={{ mt: 3, mb: 2 }}>
                Sign In
                </Button>
                <h6>New User? <a href="/register">Create your account</a> </h6>
            </Box>
            </Box>
        </Container>
        </div>
    )
}
