import React, { Component } from 'react'
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import {UserContext} from "./UserContext"
import axios from 'axios';
import { Link } from 'react-router-dom';

export class Login extends Component {
    // Page connects to eachother, data changes in one page will change in others
    
    checkUser = async (username, password) => {
        const res = await axios.get('http://localhost:3001/users');
        const { data } = res;
        var arr = Object.values(data);
        for (var i = 0; i < arr.length; i++) {
            console.log(arr[i].username, arr[i].password);
            if (arr[i].username === username && arr[i].password === password) {
                return true;
            }
        }
        return false;
    }

    handleSubmit = async (e) => {
        const {username, password} = e.data;
        const exist = await this.checkUser(username, password);
        if (exist === true) {
            const token = await axios.post('http://localhost:3001/users/login', {username, password});
            console.log(token);

        }

    }

    render() {
        return (
            <div>
            <Container component="main" maxWidth="xs">
                <CssBaseline />
                <Box
                sx={{
                    marginTop: 8,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                }}
                >
                <Typography component="h1" variant="h3">
                    Sign In
                </Typography>
                <Box component="form" onSubmit={this.handleSubmit} noValidate sx={{ mt: 1 }}>
                    <TextField
                    margin="normal"
                    required
                    fullWidth
                    id="username"
                    label="Username"
                    name="username"
                    autoComplete="username"
                    autoFocus
                    />
                    <TextField
                    margin="normal"
                    required
                    fullWidth
                    name="password"
                    label="Password"
                    type="password"
                    id="password"
                    autoComplete="current-password"
                    />
                    <FormControlLabel
                    control={<Checkbox value="remember" color="primary" />}
                    label="Remember me"
                    />
                    <Button type="submit" fullWidth variant="outlined" sx={{ mt: 3, mb: 2 }}>
                    Sign In
                    </Button>
                    <h6>New User? <a href="/register">Create your account</a> </h6>
                </Box>
                </Box>
            </Container>
            </div>
        )
    }
}

export default Login
