import React, { Component, useContext, useState } from 'react'
import axios from 'axios';
import { connect } from 'react-redux'
import { addToCart } from './actions/cartActions'
import { initState as state } from '../components/reducers/cartReducer';
import { UserContext } from "../UserContext"

const contains = (item, arr) => {
    if (arr === null) {
        return false;
    }
    console.log("Content of the array: ", arr);
    console.log("The Title of the searched thing is: ", item);
    for (var i = 0; i < arr.length; i++) {
        if (arr[i].name === item) {
            return true;
        }
    }
    return false;
}

const handleClick = async (props, item, user) => {
    props.addToCart(item.id);
    if (user === null) return;
    var arr = null;
    const resp = await axios.get('http://localhost:3001/items')
    arr = Object.values(resp.data);
    for (var i = 0; i < state.items.length; i++) {
        console.log("Title: ", state.items[i].title);
        const existed = contains(state.items[i].title, arr);
        console.log("Does it exist: ", existed);
        if (existed === false) {
            var formData = new FormData();
            var file = new File(["foo"], "foo.txt", {
                type: "text/plain",
            });
            const img = file;
            const id = user.id;
            const title = state.items[i].title;
            const description = state.items[i].desc;
            const price = state.items[i].price;
            formData.append("image", file);
            formData.append("id", user.id);
            formData.append("name", state.items[i].title);
            formData.append("desc", state.items[i].desc);
            formData.append("price", state.items[i].price);
            axios.post('http://localhost:3001/items/add', formData, 
            {
                headers: {
                    'Content-Type': 'multipart/form-data; boundary=<calculated when request is sent>'
                }
            })
            .then(response => { 
                console.log(response)
            })
            .catch(error => {
                console.log(error.response)
            });
        }
    }

    // if (user !== null) {
    //     var item = { user.id,  addedItem.id};
    //     axios.post('http://localhost:3001/users/addItem', item).then(() => {
    //         console.log("Item have been added to the user's backend");
    //     })
    // }
}

function Home(props) {
    const { user } = useContext(UserContext);
    console.log(user);
    let itemList = props.items.map(item => {
        return(
            <div className="card" key={item.id}>
                    <div className="card-image">
                        <img className="itemsImage" src={item.img} alt={item.title}/>
                        <span className="card-title title">{item.title}</span>
                        <span to="/" className="btn-floating halfway-fab waves-effect waves-light indigo darken-4" onClick={()=>{handleClick(props, item, user)}}><i className="large material-icons">shopping_cart</i></span>
                    </div>
    
                    <div className="card-content">
                        <p className="description">{item.desc}</p>
                        <p className="price"><b>Price: {item.price}$</b></p>
                    </div>
             </div>
        )
    })

    return(
        <div className="container">
            <br/> <br/> <br/>
            <h3 className="center headline">116 Tech Shop</h3>
            <h6 className="center headline">Everything Tech-Related </h6>
            <hr/>
            <div className="box">
                {itemList}
            </div>
        </div>
    )
}

const mapStateToProps = (state)=>{
    return {
      items: state.items
    }
}
const mapDispatchToProps= (dispatch)=>{
    
    return{
        addToCart: (id)=>{dispatch(addToCart(id))}
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Home);
