import React, { Component, useContext, useState } from 'react'
import axios from 'axios';
import { connect } from 'react-redux'
import { addToCart } from './actions/cartActions'
import { initState as state } from '../components/reducers/cartReducer';
import { UserContext } from "../UserContext"

const contains = (item, arr) => {
    if (arr === null) {
        return false;
    }
    for (var i = 0; i < arr.length; i++) {
        if (arr[i].id === item) {
            return true;
        }
    }
    return false;
}

const handleClick = (props, item, user)=>{
    props.addToCart(item.id);
    console.log("The value of user id is current: ", user);
    if (user === null) return;
    var arr = null;
    axios.get('http://localhost:3001/items').then(resp => {
        console.log(resp);
        arr = Object.values(resp.data);
        console.log(arr);
    }).then(() => {
        for (var i = 0; i < state.items.length; i++) {
            console.log(state.items[i]);
            if (contains(state.items[i].id, arr) === false) {
                const img = user.id;
                const id = user.id;
                const title = state.items[i].title;
                const description = state.items[i].desc;
                const price = state.items[i].price;
                const item = {img, id, title, description, price};
                console.log(item);
                axios.post('http://localhost:3001/items/add', item).then(resp => {
                    console.log("Add new items to backend");
                })
            }
        }
    }).catch(() => console.log("Adding items to backend failed"));

    // if (user !== null) {
    //     var item = { user.id,  addedItem.id};
    //     axios.post('http://localhost:3001/users/addItem', item).then(() => {
    //         console.log("Item have been added to the user's backend");
    //     })
    // }
}

function Home(props) {
    const { user } = useContext(UserContext);
    console.log(user);
    let itemList = props.items.map(item => {
        return(
            <div className="card" key={item.id}>
                    <div className="card-image">
                        <img className="itemsImage" src={item.img} alt={item.title}/>
                        <span className="card-title title">{item.title}</span>
                        <span to="/" className="btn-floating halfway-fab waves-effect waves-light indigo darken-4" onClick={()=>{handleClick(props, item, user)}}><i className="large material-icons">shopping_cart</i></span>
                    </div>
    
                    <div className="card-content">
                        <p className="description">{item.desc}</p>
                        <p className="price"><b>Price: {item.price}$</b></p>
                    </div>
             </div>
        )
    })

    return(
        <div className="container">
            <br/> <br/> <br/>
            <h3 className="center headline">116 Tech Shop</h3>
            <h6 className="center headline">Everything Tech-Related </h6>
            <hr/>
            <div className="box">
                {itemList}
            </div>
        </div>
    )
}

const mapStateToProps = (state)=>{
    return {
      items: state.items
    }
}
const mapDispatchToProps= (dispatch)=>{
    
    return{
        addToCart: (id)=>{dispatch(addToCart(id))}
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Home);
