import React, { Component } from 'react';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import axios from 'axios';
import { Redirect } from 'react-router';

const theme = createTheme();

export class Register extends Component {
  constructor(props) {
    super(props);
    
    this.state = {
      email: '',
      username: '',
      password: '',
    }
    this.registered = false;
  }

  checkExisted = (email, username) => {
    axios.get('http://localhost:3001/users')
      .then(response => {
        var arr = Object.values(response.data);
        for (var i = 0; i < arr.length; i++) {
          console.log(arr[i].email, arr[i].username);
          if (email === arr[i].email || username === arr[i].username) {
            console.log("There is someone similar to me!");
            return true;
          }
        }
    });
    return false;
  }

  changeHandler = (e) => {
    this.setState({[e.target.name]: e.target.value });
  }

  submitHandler = (e) => {
    e.preventDefault();
    console.log(this.state);
    this.checkExisted(this.state.email, this.state.username)
    .then(existed => {
      console.log("There already existed user: ", existed);
      if (existed === false) {
      axios.post('http://localhost:3001/users/add', this.state)
      .then(response => {
        console.log(response);
        this.registered = true;
        // window.location.href = "/login";
      })
      .catch(err => {
        console.log(err);
      })
    } else {
      console.log("This email or username have already been used");
    }
    });
  }

  render() {
    if (this.registered === true) {
      return <Redirect to="/"/>
    }
    return (
      <ThemeProvider theme={theme}>
        <Container component="main" maxWidth="xs">
          <CssBaseline />
          <Box
            sx={{
              marginTop: 8,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
            }}
          >
            <Typography component="h1" variant="h4">
              Register
            </Typography>
            <Box component="form" onSubmit={this.submitHandler} noValidate sx={{ mt: 1 }}>
              <TextField
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email"
                name="email"
                type="email"
                autoComplete="email"
                autoFocus
                onChange={this.changeHandler}
              />
              <TextField
                margin="normal"
                required
                fullWidth
                id="username"
                label="Username"
                name="username"
                type="username"
                autoComplete="username"
                autoFocus
                onChange={this.changeHandler}
              />
              <TextField
                margin="normal"
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password"
                autoComplete="current-password"
                onChange={this.changeHandler}
              />
              <Button type="submit" fullWidth variant="outlined" sx={{ mt: 3, mb: 2 }}>
                Register!
              </Button>
            </Box>
          </Box>
        </Container>
      </ThemeProvider>
    );
  }
}

export default Register
