import React, { useContext } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { removeItem,addQuantity,subtractQuantity} from './actions/cartActions';
import Recipe from './Recipe';
import {UserContext} from "../UserContext";
import image from "../images/noproduct.png"

const retrieveUserData = async (user) => {
    const user_id = user.id;
    const res = await axios.get(`http://localhost:3001/users/get/${id}`, {
        params: {
            id: user_id
        }
    });
    return res.data.products;
    console.log(res.data.products);
    console.log("Retrieved item for user ", user_id, "  successfully!");
}

const handleRemove = async (props, product_id, user) => {
    if (user === null) {
        props.removeItem(product_id);
        return;
    }
    const userId = user.id;
    const productId = product_id;
    const data = {userId, productId}
    const res = await axios.post(`http://localhost:3001/users/removeItem`, data);
    console.log(res);
}
//to add the quantity
const handleAddQuantity = (props, id)=>{
    props.addQuantity(id);
}
//to substract from the quantity
const handleSubtractQuantity = (props, id)=>{
    props.subtractQuantity(id);
}

function Cart(props) {
    const { user } = useContext(UserContext);
    var addedItems = null;
    var itemsList = null;
    retrieveUserData(user).then((retrievedProducts) => {
        itemsList = retrievedProducts;
    })
    if (user != null) {
        addedItems = itemsList.length ? ( itemsList.map(item => {
            console.log("Inside of mapping: ", item);
            return (
                <li className="collection-item avatar" key={item._id}>
                    <div className="item-img"> 
                        <img src={image} alt={image} className=""/>
                    </div>
                    <div className="item-desc">
                        <span className="title">{item.name}</span>
                        <p className="description">{item.desc}</p>
                        <p className="price"><b>Price: ${item.price}</b></p> 
                        <p>
                            <b>Quantity: 1</b> 
                        </p>
                        <button className="waves-effect waves-light btn blue remove" onClick={()=>{handleRemove(props, item.id, user)}}>Remove</button>
                    </div>
                </li> 
            )
        })) : ( <p>There is nothing inside your cart. Click <a href="/">here</a> to add some to it! </p> );
    } else {
            addedItems = props.items.length ? ( props.items.map(item => {
                return(
                    <li className="collection-item avatar" key={item.id}>
                        <div className="item-img"> 
                            <img src={item.img} alt={item.img} className=""/>
                        </div>
                    
                        <div className="item-desc">
                            <span className="title">{item.title}</span>
                            <p className="description">{item.desc}</p>
                            <p className="price"><b>Price: {item.price}$</b></p> 
                            <p>
                                <b>Quantity: {item.quantity}</b> 
                            </p>
                            <div className="add-remove">
                                <Link to="/cart"><i className="material-icons" onClick={()=>{handleAddQuantity(props, item.id, user)}}>arrow_drop_up</i></Link>
                                <Link to="/cart"><i className="material-icons" onClick={()=>{handleSubtractQuantity(props, item.id)}}>arrow_drop_down</i></Link>
                            </div>
                            <button className="waves-effect waves-light btn blue remove" onClick={()=>{handleRemove(props, item.id, user)}}>Remove</button>
                        </div>
                    </li> 
                )
                })
            ): ( <p>There is nothing inside your cart. Click <a href="/">here</a> to add some to it! </p> );
            console.log("******************* This is the added items: ", addedItems);
    }
        return(
            <div className="container">
                <div className="cart">
                    <h5 className="description">You have ordered:</h5>
                    <ul className="collection">
                        {addedItems}
                    </ul>
                </div> 
                <Recipe />          
            </div>
        )
}

const mapStateToProps = (state)=>{
    return{
        items: state.addedItems,
        addedItems: state.addedItems
    }
}
const mapDispatchToProps = (dispatch)=>{
    return{
        removeItem: (id)=>{dispatch(removeItem(id))},
        addQuantity: (id)=>{dispatch(addQuantity(id))},
        subtractQuantity: (id)=>{dispatch(subtractQuantity(id))}
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Cart)
