import React, { useContext, useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { removeItem,addQuantity,subtractQuantity} from './actions/cartActions';
import Recipe from './Recipe';
import {UserContext} from "../UserContext";
import image from "../images/noproduct.png"

const retrieveUserData = async (user_id) => {
    const id = user_id;
    const res = await axios.get(`http://localhost:3001/users/get/${id}`, {
        params: {
            id: user_id
        }
    });
    return res.data.products;
}

const handleRemove = async (props, item, user) => {
    if (user === null) {
        props.removeItem(item.id);
        return;
    }
    const user_id = user.id;
    const name = item.name;
    const response = axios.get(`http://localhost:3001/items`);
    const array = response.data;
    const productId = null;
    for (var i = 0; i < array.length; i++) {
        if (array[i].name === name) {
            productId = array[i]._id;
            break;
        }
    }
    console.log(`Item.id vs productId: ${item.id} vs ${productId}`);

    const deleteItem = {user_id, productId}
    const res = axios.post("http://localhost:3001/users/removeAll", deleteItem);
}

//to add the quantity
const handleAddQuantity = async (props, item, user) => {
    if (user === null) {
        props.addQuantity(item.id);
        return;
    }
    const user_id = user.id;
    const name = item.name;
    const response = axios.get(`http://localhost:3001/items`);
    const array = response.data;
    const productId = null;
    for (var i = 0; i < array.length; i++) {
        if (array[i].name === name) {
            productId = array[i]._id;
            break;
        }
    }
    console.log(`Item.id vs productId: ${item.id} vs ${productId}`);

    const addItem = {user_id, productId}
    const res = axios.post("http://localhost:3001/users/addItem", addItem);
    console.log(res);
}

//to substract from the quantity
const handleSubtractQuantity = async (props, item, user)=>{
    if (user === null) {
        props.subtractQuantity(item.id);
        return;
    }
    const user_id = user.id;
    const name = item.name;
    const response = axios.get(`http://localhost:3001/items`);
    const array = response.data;
    const productId = null;
    for (var i = 0; i < array.length; i++) {
        if (array[i].name === name) {
            productId = array[i]._id;
            break;
        }
    }
    console.log(`Item.id vs productId: ${item.id} vs ${productId}`);

    const deleteItem = {user_id, productId}
    const res = axios.post("http://localhost:3001/users/removeItem", deleteItem);
}

function Cart(props) {
    const { user } = useContext(UserContext);
    let [chars, setChars] = useState([]);
    var addedItems = null;
    var itemsList = null;
    useEffect(() => {
        async function fetchMyAPI() {
            let response = await axios.get(`http://localhost:3001/users/get/${user.id}`, {
                params: {
                    id: user.id
                }
            });
            setChars(response.data.cart)
            console.log("Value of first chars: ", chars);
        }
        fetchMyAPI()
    }, [])

    if (user != null) {
            console.log("This is the second chars call: ", chars);
            addedItems = chars.length ? ( chars.map(item => {
            return (
                <li className="collection-item avatar" key={item._id}>
                    <div className="item-img"> 
                        <img src={image} alt={image} className=""/>
                    </div>
                    <div className="item-desc">
                        <span className="title">{item.name}</span>
                        <p className="description">{item.desc}</p>
                        <p className="price"><b>Price: ${item.price}</b></p> 
                        <p>
                            <b>Quantity: {item.quantity}</b> 
                        </p>
                        <div className="add-remove">
                            <Link to="/cart"><i className="material-icons" onClick={()=>{handleAddQuantity(props, item, user)}}>arrow_drop_up</i></Link>
                            <Link to="/cart"><i className="material-icons" onClick={()=>{handleSubtractQuantity(props, item)}}>arrow_drop_down</i></Link>
                        </div>
                        <button className="waves-effect waves-light btn blue remove" onClick={()=>{handleRemove(props, item.id, user)}}>Remove</button>
                    </div>
                </li> 
            )
            })) : ( <p>There is nothing inside your cart. Click <a href="/">here</a> to add some to it! </p> );
    } else {
            addedItems = props.items.length ? ( props.items.map(item => {
                return(
                    <li className="collection-item avatar" key={item.id}>
                        <div className="item-img"> 
                            <img src={item.img} alt={item.img} className=""/>
                        </div>
                    
                        <div className="item-desc">
                            <span className="title">{item.title}</span>
                            <p className="description">{item.desc}</p>
                            <p className="price"><b>Price: {item.price}$</b></p> 
                            <p>
                                <b>Quantity: {item.quantity}</b> 
                            </p>
                            <div className="add-remove">
                                <Link to="/cart"><i className="material-icons" onClick={()=>{handleAddQuantity(props, item.id, user)}}>arrow_drop_up</i></Link>
                                <Link to="/cart"><i className="material-icons" onClick={()=>{handleSubtractQuantity(props, item.id)}}>arrow_drop_down</i></Link>
                            </div>
                            <button className="waves-effect waves-light btn blue remove" onClick={()=>{handleRemove(props, item.id, user)}}>Remove</button>
                        </div>
                    </li> 
                )
                })
            ): ( <p>There is nothing inside your cart. Click <a href="/">here</a> to add some to it! </p> );
            console.log("******************* This is the added items: ", addedItems);
    }
        return(
            <div className="container">
                <div className="cart">
                    <h5 className="description">You have ordered:</h5>
                    <ul className="collection">
                        {addedItems}
                    </ul>
                </div>
                <Recipe />          
            </div>
        )
}

const mapStateToProps = (state)=>{
    return{
        items: state.addedItems,
        addedItems: state.addedItems
    }
}
const mapDispatchToProps = (dispatch)=>{
    return{
        removeItem: (id)=>{dispatch(removeItem(id))},
        addQuantity: (id)=>{dispatch(addQuantity(id))},
        subtractQuantity: (id)=>{dispatch(subtractQuantity(id))}
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Cart)
